/** An enum for event types consumed by the realtime module */
export declare enum EventTypes {
    MESSAGE = "message",
    EVENT = "event",
    CONVERSATION = "conversation",
    MESSAGE_RECEIPT = "message_receipt",
    STATUS = "status"
}
/** An enum for actions consumed by the realtime module  */
export declare enum EventActions {
    CREATE = "create",
    UPDATE = "update"
}
/**
 * An interface for an event carrying type, action and timestamp that might be
 * send out by realtime
 */
export interface Event {
    type: EventTypes;
    action?: EventActions;
    timestamp?: string;
}
/**
 * A typed event that holds additional data as paylod
 */
export interface EventWithData<T> extends Event {
    data: T;
}
